mapper.select:
SELECT snippet_id as snippetId, content, language
FROM snippet_codes
WHERE snippet_id = #{snippetId}

SELECT snippet_id as snippetId, content
FROM snippet_texts
WHERE snippet_id = #{snippetId}

SELECT snippet_id as snippetId, image_url as imageUrl, alt_text as altText
FROM snippet_images
WHERE snippet_id = #{snippetId}
