mapper.select:
SELECT DISTINCT s.snippet_id, s.user_id, s.folder_id, s.color_id, s.source_url, s.type, s.memo, s.created_at, s.updated_at, s.visibility, COALESCE((SELECT COUNT(*) FROM likes l WHERE l.snippet_id = s.snippet_id), 0) as like_count FROM snippets s LEFT JOIN snippet_tags st ON s.snippet_id = st.snippet_id LEFT JOIN tags t ON st.tag_id = t.tag_id WHERE s.visibility = 1
AND s.type = #{type}
AND (s.memo LIKE CONCAT('%', #{keyword}, '%') OR s.type LIKE CONCAT('%', #{keyword}, '%'))
AND t.name = #{tagName}
AND (SELECT COUNT(*) FROM likes l WHERE l.snippet_id = s.snippet_id) >= #{minLikes}
ORDER BY like_count DESC, s.created_at DESC LIMIT 100

mapper.update:
UPDATE snippets
SET like_count = #{likeCount}
WHERE snippet_id = #{snippetId}
